export const handleChange = (e, formState, setFormState) => {

    console.log(e.target.value)
    const { name, value } = e.target;
    console.log('erer', {name, value})

    if (name === 'name') setFormState({...formState, [name]: value})
    if (name === 'email') setFormState({...formState, [name]: value})
    if (name === 'password') setFormState({...formState, [name]: value})
    if (name === 'occupation') setFormState({...formState, [name]: value})
    if (name === 'state'){ 
        console.log('FLKMVFLKMVSRKBMNVDKF', value)
        setFormState({...formState, [name]: value})
    }
}

