import React, { useState, useEffect } from 'react'
import { getDataCache } from '../axios-api-setup'
import { Form, NameField, SubmitButton, EmailField, PasswordField, OccupationField, StateField, handleChange, handleSubmit } from './index'

const MyForm = () => {
    // load cache data from API
    useEffect(() => {
        getDataCache().then(res => {
            setOccupationOptions(res.occupations)
            setStateOptions(res.states.map(state => state.abbreviation))
        })
    }, [])

    const [formState, setFormState] = useState({
        name: '',
        email: '',
        password: '',
        selectedOccupation: '',
        selectedState: '',
    })

    const [ occupationOptions, setOccupationOptions ] = useState([''])
    const [ stateOptions, setStateOptions ] = useState([''])
    const [ selectedOccupation, setSelectedOccupation ] = useState('')
    const [ selectedState, setSelectedState ] = useState('')

    // const [ name, setName ] = useState('')
    // const [ email, setEmail ] = useState('')
    // const [ password, setPassword ] = useState('')


    // const handleChange = (e) => {
    //     const { name, value } = e.target;
    //     if (name === 'name') setName(value);
    //     if (name === 'email') setEmail(value);
    //     if (name === 'password') setPassword(value);
    //     if (name === 'occupation') setSelectedOccupation(selectedOccupation);
    //     if (name === 'state') setSelectedState(selectedState);
    // }

    // const handleSubmit = (e) => {
    //     e.preventDefault()
    //     console.log('Name:', name)
    //     console.log('Email:', email)
    //     console.log('Password:', password)
    //     console.log('Occupation:', selectedOccupation)
    //     console.log('State:', selectedState)
    // }

    return (
        // <Form onSubmit={handleSubmit}></Form>
        <Form onSubmit={(event) => handleSubmit(event, formState)}>
            <NameField name={formState.name} handleChange={(event) => handleChange(event, setFormState)} />
            <EmailField email={formState.email} handleChange={(event) => handleChange(event, setFormState)} />
            <PasswordField password={formState.password} handleChange={(event) => handleChange(event, setFormState)}/>
            <OccupationField occupationOptions={occupationOptions} setSelectedOccupation={setSelectedOccupation}/>
            <StateField stateOptions={stateOptions} setSelectedState={setSelectedState}/>
            <SubmitButton label={'Submit'}/>
        </Form>
    )
}

export default MyForm;
