import React from 'react';
import { Form } from 'react-bootstrap';
import '../styles/App.css'


const TextInputField = ({ valid, label, name, type, value, placeholder, handleChange, }) => (
    <Form.Group controlId={name.concat("-textInput")}>
        <Form.Label>{label}</Form.Label>
        <Form.Control 
            required
            className={`form-input ${valid ? '' : 'invalid'}`}
            type={type} 
            value={value}
            placeholder={placeholder}
            name={name} 
            onChange={handleChange}
        />
        <Form.Control.Feedback className='error-msg' type="invalid">
            {name === 'name' && 'Enter a Full name'}
            {name === 'email' && 'Enter valid Email'}
            {name === 'password' && 'Enter between 8 and 20 characters'}
            Invalid input
        </Form.Control.Feedback>
    </Form.Group>
);

export default TextInputField;
