import { checkFullForm } from './FormChecker'


export const handleChange = (e, formState, setFormState) => {
    const { name, value } = e.target;
    setFormState({...formState, [name]: value})
}

export const handleSubmit = (e, formState, setFormState, setValidated) => {

    // if (checkFullForm(formState) === false) {
    //     console.log('FRKJERNFJKEN')
    //   e.preventDefault();
    //   e.stopPropagation();

    // }
    const form = e.currentTarget;

    if (form.checkValidity() === false) {
        e.preventDefault();
        e.stopPropagation();
    }


    setValidated(true);
    setFormState({
        name: '',
        email: '',
        password: '',
        occupation: '',
        state: '',
    })   

    console.log(formState)
}
