import Axios from 'axios';
import axiosCacheAdapter from 'axios-cache-adapter';
import { useState, useEffect } from "react";

const cache = axiosCacheAdapter.setupCache({
    maxAge: 15 * 60 * 1000
});

const api = Axios.create({
    adapter: cache.adapter
});

export function useFormData() {
    const [data, setData] = useState(null);
    const [status, setStatus] = useState('loading');
    useEffect(() => {
        api
            .get('https://frontend-take-home.fetchrewards.com/form')
            .then(response => {
                // setData(response.data);
                // setStatus('success');

                if (response.config.cache) {
                    console.log('Data loaded from cache');
                } else {
                    console.log('Data loaded from server');
                    console.log(response.config.cache.store)
                    response.config.cache.store.set('https://frontend-take-home.fetchrewards.com/form', response.data);
                    setData(response.data);
                    setStatus('success');
                }

            })
            .catch(error => {
                setStatus('error');
            });
            console.log('SENT REQUEST')
    }, []);
    return { data, status };
}

