export const handleChange = (e, setFormState, formState) => {
    const { name, value } = e.target;
    if (name === 'name') setFormState({...formState, [name]: value});
    if (name === 'email') setFormState();
    if (name === 'password') setFormState();
    if (name === 'occupation') setFormState();
    if (name === 'state') setFormState();
}

export const handleSubmit = (e, formState) => {
    e.preventDefault()
    console.log(formState)
}


