import axios from 'axios';

// encode => {}



// async function getData() {
//     const response = await axios.get("https://frontend-take-home.fetchrewards.com/form")
//     return response.data;       
// }


function getCachedData() {

}



export function getDataCache() {
    // Check if the data is already in localStorage
    let data = localStorage.getItem("formData");

    // get hashsum
    // encode ->
    // if hashsum === encode

    // check ttl

    if (data) {
        console.log('GOT DATA FROM CACHE')
        return Promise.resolve(JSON.parse(data))
    }

    // If not, send a request to the API
    console.log('MADE A REQUEST')
    return axios
        .get("https://frontend-take-home.fetchrewards.com/form")
        .then(response => {

            // 
            // const res = {
            //     ...response.data,
            //     states: response.data.states.map(state => state.abbreviation),
            // }

            // Store the data in localStorage
            localStorage.setItem("formData", JSON.stringify(response.data));
            // encode -> save (formDataHash)
            return response.data;
        });
}
