import React, { useState, useEffect } from 'react'
import { getDataCache } from '../axios-api-setup'
import { Form, NameField, SubmitButton, EmailField, PasswordField, OccupationField, StateField, handleChange, handleSubmit } from './index'
import '../styles/App.css'

const MyForm = () => {
    const [ occupationOptions, setOccupationOptions ] = useState([''])
    const [ stateOptions, setStateOptions ] = useState([''])

    // load cache data from API
    useEffect(() => {
        getDataCache().then(res => {
            setOccupationOptions(res.occupations)
            setStateOptions(res.states.map(state => state.abbreviation))
        })
    }, [])

    // final form data
    const [formState, setFormState] = useState({
        name: '',
        email: '',
        password: '',
        occupation: '',
        state: '',
    })

    return (
        <Form className='form-container' onSubmit={(event) => handleSubmit(event, formState, setFormState)}>
            <NameField name={formState.name} handleChange={(event) => handleChange(event, formState, setFormState)} />
            <EmailField email={formState.email} handleChange={(event) => handleChange(event, formState, setFormState)} />
            <PasswordField password={formState.password} handleChange={(event) => handleChange(event, formState, setFormState)}/>
            <OccupationField occupationOptions={occupationOptions} handleChange={(event) => handleChange(event, formState, setFormState)}/>
            <StateField stateOptions={stateOptions} handleChange={(event) => handleChange(event, formState, setFormState)}/>
            <SubmitButton label={'Submit'}/>
        </Form>
    )
}

export default MyForm;
