import React, { useState, useEffect } from 'react'
import { getCachedData } from '../axios-api-setup'
import { Form, NameField, SubmitButton, EmailField, PasswordField, OccupationField, StateField, handleChange, handleSubmit } from './index'
import '../styles/App.css'

const MyForm = () => {
    const [ occupationOptions, setOccupationOptions ] = useState([''])
    const [ stateOptions, setStateOptions ] = useState([''])
    const submit = (event) => handleSubmit(event, formState, setFormState)
    const change = (event) => handleChange(event, formState, setFormState)

    // final form data
    const [formState, setFormState] = useState({
        name: '',
        email: '',
        password: '',
        occupation: '',
        state: '',
    })

    // load cache data from API
    useEffect(() => {
        getCachedData().then(res => {
            console.log('here', res)
            setOccupationOptions(res.occupations)
            setStateOptions(res.states)
            console.log('KOKO')
        })
        console.log('LOL')
        // console.log('JOJOJ', g)
        // getDataCache().then(res => {
        //     setOccupationOptions(res.occupations)
        //     setStateOptions(res.states.map(state => (state.abbreviation).concat(" - ", (state.name)) ))
        // })
    }, [])

    return (
        <Form className='form-container' onSubmit={submit}>
            <NameField name={formState.name} handleChange={change} />
            <EmailField email={formState.email} handleChange={change} />
            <PasswordField password={formState.password} handleChange={change}/>
            <OccupationField occupationOptions={occupationOptions} handleChange={change}/>
            <StateField stateOptions={stateOptions} handleChange={change}/>
            <SubmitButton label={'Submit'}/>
        </Form>
    )
}

export default MyForm;
