import React from 'react';
import { Form } from 'react-bootstrap';
import '../styles/App.css'


const TextInputField = ({ valid, label, name, type, value, placeholder, handleChange, }) => (
    <Form.Group controlId={name.concat("-textInput")}>
        <Form.Label>{label}</Form.Label>
        <Form.Control 
            required
            className={`form-input ${valid ? '' : 'invalid'}`}
            type={type} 
            value={value}
            placeholder={placeholder}
            name={name} 
            onChange={handleChange}
        />
        <span className="error-msg">Invalid input</span>
        {/* <Form.Control.Feedback>Looks good!</Form.Control.Feedback> */}
        {/* <Form.Control.Feedback className='form-input:is-invalid' type="invalid">
            Invalid input.
        </Form.Control.Feedback> */}
    </Form.Group>
);

export default TextInputField;
