    //     data = await getData();
    //     data = {
    //         data: {
    //             ...data,
    //             occupations: data.occupations,
    //             states: data.states.map(state => (state.abbreviation).concat(" - ", (state.name)) ),
    //         },
    //         expiration: currentTime + ttl
    //     };
    //     localStorage.setItem("formData", JSON.stringify(data));
    //     return data.data;
    // }


    return data.then(res => {
        res = {
            ...res.data,
            occupations: res.occupations,
            states: res.states.map(state => (state.abbreviation).concat(" - ", (state.name)) ),
        };

        localStorage.setItem("formData", JSON.stringify(res));

        return res;
    });
}
