export const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'name') setFormState.name(value);
    if (name === 'email') setFormState.email(value);
    if (name === 'password') setFormState.password(value);
    if (name === 'occupation') setFormState(selectedOccupation);
    if (name === 'state') setSelectedState(selectedState);
}


