import React, { useState, useEffect } from "react"
// import { Form, Button } from 'react-bootstrap'
import { getDataCache } from '../axios-api-setup'
import Form from './Form'

const MyForm = () => {
    useEffect(() => {
        getDataCache().then(res => {
            setOccupationOptions(res.occupations)
            setStateOptions(res.states.map(state => state.abbreviation))
        });
    }, []);

    const [ name, setName ] = useState('')
    const [ email, setEmail ] = useState('')
    const [ password, setPassword ] = useState('')
    const [ selectedOccupation, setSelectedOccupation ] = useState('');
    const [ selectedState, setSelectedState ] = useState('');
    const [ occupationOptions, setOccupationOptions ] = useState([''])
    const [ stateOptions, setStateOptions ] = useState([''])

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'name') setName(value);
        if (name === 'email') setEmail(value);
        if (name === 'password') setPassword(value);
        if (name === 'occupation') setSelectedOccupation(selectedOccupation);
        if (name === 'state') setSelectedState(selectedState);
    }

    const handleSubmit = (e) => {
        // handle form submission
    }

    return (
        <Form onSubmit={handleSubmit}>
            <NameField />
            {/* <EmailField />
            <PasswordField />
            <OccupationField />
            <StateField />
            <SubmitButton /> */}
        </Form>
    )
}

export default MyForm;
