import Req from './requester'

export default class CachedRequester extends Req {
    static ttl = 10 * 1000 // 5s

    static cache = {
        timeCreated: Date.now(),
        data: null,
    }

    static setupDone = false

    static setup() {
        this.setupDone = true

        const res_raw = localStorage.getItem('reqCache')
        if (res_raw) {
            this.cache = JSON.parse(res_raw)
        }

        this.save()
    }

    static save() {
        localStorage.setItem('reqCache', JSON.stringify(this.cache))
    }

    static async getData() {
        if (!this.setupDone) 
            this.setup()

        if (this.cache.data && Date.now() < this.cache.timeCreated + this.ttl)
            return this.cache.data

        const res = await Req.getData()
        this.cache = {
            timeCreated: Date.now(),
            data: res,
        }
        this.save()

        return res
    }
}
