import React from "react";
import Axios from 'axios';
import axiosCacheAdapter from 'axios-cache-adapter';
import { useState, useEffect } from "react";
import { Form, Button } from 'react-bootstrap'

const cache = axiosCacheAdapter.setupCache({
    maxAge: 15 * 60 * 1000
});

const api = Axios.create({
    adapter: cache.adapter
});

function useFormData() {
    const [data, setData] = useState(null);
    const [status, setStatus] = useState('loading');

    useEffect(() => {
        // setData(() => getRequest('https://frontend-take-home.fetchrewards.com/form'))
        api
            .get('https://frontend-take-home.fetchrewards.com/form')
            .then(response => {
                setData(response.data);
                setStatus('success');
            })
            .catch(error => {
                setStatus('error');
            });
            console.log('SENT REQUEST')
    }, []);
    return { data, status };
}

function sendFormData(res) {


}

const MyForm = () => {
    const { data, status } = useFormData()
    const [ name, setName ] = useState('')
    const [ email, setEmail ] = useState('')
    const [ password, setPassword ] = useState('')
    const [ selectedOccupation, setSelectedOccupation ] = useState('');
    const [ selectedState, setSelectedState ] = useState('');
    let occupationOptions = ['']
    let stateOptions = ['']
    const finalRes = {
        'name': '',
        "email": '',
        "password": '',
        "occupation": '',
        "state": ''
    }

    if (status === 'success') {
        occupationOptions = data.occupations
        stateOptions = data.states.map(state => state.abbreviation)
    }

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'name') setName(value);
        if (name === 'email') setEmail(value);
        if (name === 'password') setPassword(value);
        if (name === 'occupation') setSelectedOccupation(selectedOccupation);
        if (name === 'state') setSelectedState(selectedState);
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        sendFormData()
        console.log('Name:', name)
        console.log('Email:', email)
        console.log('Password:', password)
        console.log('Occupation:', selectedOccupation)
        console.log('State:', selectedState)
    }

    return (
        <Form onSubmit={handleSubmit}>
            {/* NAME */}
            <Form.Group controlId="formName">
                <Form.Label>Full Name</Form.Label>
                <Form.Control 
                    type="text" 
                    placeholder="Enter Full Name" 
                    name="name" 
                    value={name}
                    onChange={handleChange}
                />
            </Form.Group>

            {/* EMAIL */}
            <Form.Group controlId="formEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control 
                    type="email" 
                    placeholder="Enter Email" 
                    name="email" 
                    value={email} 
                    onChange={handleChange}
                />
            </Form.Group>

            {/* PASSWORD */}
            <Form.Group controlId="formPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control 
                    type="password" 
                    placeholder="Create Password" 
                    name="password" 
                    value={password} 
                    onChange={handleChange}
                />
            </Form.Group>

            {/* OCCUPATIOIN */}
            <Form.Group controlId="formOccupation">
                <Form.Label>Occupation</Form.Label>
                <Form.Control as="select" defaultValue='' onChange={e => setSelectedOccupation(e.target.value)}>
                    <option value=''>Select an occupation</option>
                    {occupationOptions.map(occupationOption => (
                        <option key={occupationOption}> {occupationOption} </option>
                    ))}
                </Form.Control>

            </Form.Group>


            {/* STATE */}
            <Form.Group controlId="formState">
                <Form.Label>State</Form.Label>
                <Form.Control as="select" defaultValue='' onChange={e => setSelectedState(e.target.value)}>
                    <option value=''>Select a state</option>
                    {stateOptions.map(stateOption => (
                        <option key={stateOption}>{stateOption}</option>
                    ))}
                </Form.Control>
            </Form.Group>

            <Button variant="primary" type="submit">
                Submit
            </Button>

        </Form>
    )
}

export default MyForm;
