import React, { useState, useEffect } from 'react'
import { getCachedData } from '../axios-api-setup'
import { Form, TextInputField, DropdownField, SubmitButton, handleChange, handleSubmit } from './index'
import '../styles/App.css'

const MyForm = () => {
    // final form data
    const [formState, setFormState] = useState({
        // name: '',
        // email: '',
        // password: '',
        // occupation: '',
        // state: '',

            "name": "Malika",
            "email": "eamalikaa@gmail.com",
            "password": "fefefefefef",
            "occupation": "Water Softener",
            "state": "IL - Illinois"
    })

    const [formValid, setFormValid] = useState({
        name: true,
        email: true,
        password: true,
        occupation: true,
        state: true,
    })

    const [ occupationOptions, setOccupationOptions ] = useState([''])
    const [ stateOptions, setStateOptions ] = useState([''])

    const submit = (event) => handleSubmit(event, formState, setFormState, formValid, setFormValid)
    const change = (event) => handleChange(event, formState, setFormState, formValid, setFormValid)

    // load cache data from API
    useEffect(() => {
        getCachedData().then(res => {
            setOccupationOptions(res.occupations)
            setStateOptions(res.states)
        })
    }, [])


    return (
        <Form className='form-container' noValidate onSubmit={submit}>
            <TextInputField valid={formValid.name} label='Full Name' value={formState.name} name='name' type='text' placeholder='Enter Full Name' handleChange={change} />
            <TextInputField valid={formValid.email} label='Email address' value={formState.email} name='email' type='email' placeholder='Enter Email' handleChange={change} />
            <TextInputField valid={formValid.password} label='Password' value={formState.password} name='password' type='password' placeholder='Create Password' handleChange={change} />
            <DropdownField options={occupationOptions} label='Occupation' value={formState.occupation} name='occupation' handleChange={change}/>
            <DropdownField options={stateOptions} label='State' value={formState.state} name='state' handleChange={change}/>
            <SubmitButton label={'Submit'}/>
        </Form>
    )
}

export default MyForm;
